use Joomla\CMS\Language\Text;


function joomlalogo( $sitename, $width = false ) {

 	$args = [ constant('Logo_Joomla') , 'title' => 'Joomla!', 'desc' => $sitename, 'class' => 'logo me-2', 'id' => 'joomla-logo' ];
	if ($width) { $args['size'] = $width; }

	return svg( $args );
}

function pcjlogo( $sitename, $width=false) {

	$args = [ constant('Logo_PCJ'), 'title' => 'Polskie Centrum', 'desc' => $sitename, 'class' => 'logo', 'id' => 'pcj-logo' ];
	if ($width) { $args['size'] = $width; }
	return svg( $args );
}

function svg ( $args )

{
		$uid = uID();
		$paths = $args[0][0]; // [ paths , viewbox ]
		$vb = $args[0][1];
		$size =	array_key_exists( 'size', $args ) ? $args['size'] : $vb ; // width or [ width , height ] or fallback to viewbox
		$class =	array_key_exists( 'class', $args ) ? " class='{$args['class']}'" : '' ;
		$id =	array_key_exists( 'id',	$args) ? " id='{$args['id']}'" : '' ;
		$defs =	array_key_exists( 'defs', $args ) ? "<defs>{$args['defs']}</defs>" : '' ;
		
		if (array_key_exists( 'title', $args )) { $title = "<title id='t{$uid}'>{$args['title']}</title>"; $tid = 't'.$uid; } else { $title = ''; $tid = ''; } ;
		if (array_key_exists( 'desc', $args )) { $desc = "<description	id='t{$uid}'>{$args['desc']}</description>"; $did = ' d'.$uid; } else { $desc = ''; $did = ''; } ;
		
	
	//prepare dimensions 

		if (is_int($size)) { 
				// if only width given
				$height = $size; 
				$width = ( $vb[0] / $vb[1] ) * $size;
		} 
		else { $width = $size[0]; $height = $size[1]; }

		$svg = "<svg xmlns='http://www.w3.org/2000/svg' " . $id . $class ;

		$svg .= "width='{$width}' height='{$height}' viewBox='0 0 {$vb[0]} {$vb[1]}'";

		if ( $title ) { $svg .= " aria-labelledby='{$tid}'>"; } 
		if ( $desc ) { $svg .= " aria-describedby='{$did}'>"; } 

		if (!( $title || $desc )) { $svg .= ' aria-hidden="true" focusable="false">'; }

		$svg .= $title . $desc . $defs . $paths . "</svg>";

		return $svg;
}

function smico() {	// produces small icons
					// accepts single argument or number of arguments
					// in later case order is [ name, size, title, class ]

		$args = func_get_args();

		while (!(is_string($args[0]))) {$args = $args[0];} // PHP really sucks sometimes

		$load = count($args);

		$svg = [];

		if (	$load > 3 )									{ $svg['class'] = 'smico ' . $args[3]; } else { $svg['class'] = 'smico '; }
		if (	$load > 2 ) { if (	$args[2] ) $svg['title'] = $args[2]; } // if key exists and value is not false
		if (	$load > 1 ) { if (	$args[1] ) $svg['size']	= $args[1]; } // if key exists and value is not false
		if (	$load > 0 )									{ $svg[ 0 ] = $args[0]; } 
		else { $svg[0] = '-error-'; }

		if (defined( 'SmallIconFeather-' . $svg[0] )) { $svg[0]	= constant('SmallIconFeather-' . $svg[0]); }
		elseif	(defined( 'SmallIcon-' . $svg[0] )) { $svg[0]	= constant('SmallIcon-' . $svg[0]); }
		else {	$svg = [ constant('SmallIconFeather-alert-octagon'),'title' => Text::_('TPL_SPACEZ_WRONG_ICON'), 'class' => 'smico alert-danger' ]; }
		
		return svg($svg) ;
}

function presentIcons() { // produces small icons

		$cst = array_keys(get_defined_constants(true)['user']);
		$featherIcons = ''; $otherIcons = ''; $logoIcons = '';

		foreach($cst as $name){
				if (str_starts_with( $name, 'SmallIconFeather-' )) {
						$featherIcons.= '<kbd class="m-2 pt-3 pb-5 text-center col-2"><strong>'. str_replace('SmallIconFeather-','',$name) .'</strong></br></br>' .svg([constant($name) ,'class'=>'smico','size'=>32]).'</kbd>';
				} elseif (str_starts_with( $name, 'SmallIcon-' )) {
						$featherIcons.= '<kbd class="m-2 pt-3 pb-5 text-center col-2"><strong>'. str_replace('SmallIcon-','',$name) .'</strong></br></br>' .svg([constant($name) ,'class'=>'smico','size'=>32]).'</kbd>';
				} elseif (str_starts_with( $name, 'Icon' )) {
						$otherIcons.= '<kbd class="m-2 pt-3 pb-5 text-center col-2"><strong>'. str_replace('Icon','',$name) .'</strong></br></br>' .svg([constant($name) ,'class'=>'icon','size'=>48]).'</kbd>';
				} elseif (str_starts_with( $name, 'Logo_' )) {
						$logoIcons.= '<kbd class="m-2 pt-3 pb-5 text-center col-2"><strong>'. str_replace('Logo_','',$name) .'</strong></br></br>' .svg([constant($name) ,'class'=>'icon m-0','size'=>64]).'</kbd>';
				}
		}
		$result = "<h4>Ikony Feather <small class='muted fs-6'> 24x24 </small></h4><div class='row align-items-start'>{$featherIcons}</div>";
		$result .= "<h4>Inne Ikony <small class='muted fs-6'> 128x128 </small></h4><div class='row align-items-start'>{$otherIcons}</div>";
		$result .= "<h4>Loga <small class='muted fs-6'> 196x196 lub inne </small></h4><div class='row align-items-start'>{$logoIcons}</div>";

		return $result;
}

function uID($lenght = 10) { // hashes hrtime using css id safe characters

		$result='';
		$input =	substr(hrtime(true), -5); // 5 most variable digits - last ;)
		$vocabulary = 'abcdefghijklmnopqrstuwxyzABCDEFGHIJKLMNOPQRSTUWXYZ1234567890-_';

		$dict = str_split( $vocabulary , 1 );

		while($input != '0')
		{
				$result = $dict[bcmod($input,62)].$result;
				$input = bcdiv($input,62,0);
		}
		return $result;
}


